import Chart from "../Chart/Chart";
const ExpensesChart = props =>{
    const chartDataPoints = [
        {label : 'jan' , vlaue : 0 },
        {label : 'feb' , vlaue : 0 },
        {label : 'mar' , vlaue : 0 },
        {label : 'apr' , vlaue : 0 },
        {label : 'may' , vlaue : 0 },
        {label : 'jun' , vlaue : 0 },
        {label : 'jul' , vlaue : 0 },
        {label : 'aug' , vlaue : 0 },
        {label : 'sep' , vlaue : 0 },
        {label : 'oct' , vlaue : 0 },
        {label : 'nov' , vlaue : 0 },
        {label : 'dec' , vlaue : 0 },
    ];

    for (const expense of props.expenses ){
        const expenseMonth = expense.date.getMonth();
        chartDataPoints[expenseMonth].value += props.amount ;
    }

    <Chart dataPoints = {chartDataPoints} ></Chart>
};

export default ExpensesChart;
