import {
  Stepper,
  Step,
  StepLabel,
  StepButton,
  StepConnector,
  makeStyles,
} from "@material-ui/core";

import "./Sample2.css";
const Sample2 = (props) => {
  const useStyles = makeStyles(() => ({
    root: {
      "& .MuiStepIcon-active": { color: "orange" },
      "& .MuiStepIcon-completed": { color: "orange" },
      "& .Mui-disabled .MuiStepIcon-root": { color: "cyan" },
    },
  }));

  const c = useStyles();

  return (
    <section className="sec">
      <Stepper alternativeLabel={true} className={`${c.root} ` } activeStep={props.activeStep}>
        <Step last={true}  index={3} >
          <StepLabel index={3}>ثبت چک لیست </StepLabel>
        </Step>
        <StepConnector></StepConnector>
        <Step index={2}  >
          {" "}
          <StepLabel index={2}>تعریف افراد </StepLabel>
        </Step>
        <StepConnector></StepConnector>
        <Step index={1} >
          <StepLabel index={1}>تعریف زون ها </StepLabel>
        </Step>
        <StepConnector></StepConnector>
        <Step index={0} >
          <StepLabel index={0}> اطلاعات اصلی</StepLabel>
        </Step>
      </Stepper>
    </section>
  );
};

export default Sample2;
