import { Stepper, Step  } from "react-form-stepper";
import "./Sample2.css";
const Sample2 = (props) => {
  return (
    <section className="sec">
      <Stepper
  activeStep={activeStep}
  sx={{
    "& .MuiStepConnector-line": {
      borderTopWidth: "4px",
    },
    "& .MuiStepConnector-root.Mui-active .MuiStepConnector-line": {
      borderColor: "red",
    },
    "& .MuiStepConnector-root.Mui-completed .MuiStepConnector-line": {
      borderColor: "green",
    },
 }}
 className="cn" >
        <Step  index={3} label="ثبت چک لیست" />
        <Step index={2} label="تعریف افراد" />
        <Step index={1} label="تعریف زون ها" />
        <Step className="cn2" index={0} label="اطلاعات اصلی" />
      </Stepper>
    </section>
  );
};

export default Sample2;
