/* App.js */
import React, { Component } from "react";
import CanvasJSReact from "./canvasjs.react";
import "./FirstChart.css";
var CanvasJS = CanvasJSReact.CanvasJS;
var CanvasJSChart = CanvasJSReact.CanvasJSChart;
class FirstChart extends Component {
  constructor() {
    super();
    this.toggleDataSeries = this.toggleDataSeries.bind(this);
    this.addSymbols = this.addSymbols.bind(this);
  }
  addSymbols(e) {
    var suffixes = ["", "", "M", "B"];
    var order = Math.max(Math.floor(Math.log(e.value) / Math.log(1000)), 0);
    if (order > suffixes.length - 1) order = suffixes.length - 1;
    var suffix = suffixes[order];
    return CanvasJS.formatNumber(e.value / Math.pow(1000, order)) + suffix;
  }
  toggleDataSeries(e) {
    if (typeof e.dataSeries.visible === "undefined" || e.dataSeries.visible) {
      e.dataSeries.visible = false;
    } else {
      e.dataSeries.visible = true;
    }
    this.chart.render();
  }
  render() {
    CanvasJS.addColorSet("greenShades", [
      //colorSet Array
      ":rgb(199, 208, 223)",
      "rgb(228, 111, 22)",
    ]);
    const options = {
      axisX: {
        lineColor: "transparent",
        tickLength: 0,
      },
      axisY: {
        interlacedColor: "transparent",
        tickLength: 0,
        gridColor: "transparent",
      },
      backgroundColor: "transparent",
      animationEnabled: true,
      colorSet: "greenShades",
      data: [
        {
          type: "column",
          width: "0.5rem",
          // xValueFormatString: "MMMM YYYY",
          // yValueFormatString: "$#,##0",
          dataPoints: [
            { y: 27500, label: "رمضانی" },
            { y: 29000, label: "رمضانی" },
            { y: 22000, label: "رمضانی" },
            { y: 26500, label: "رمضانی" },
            { y: 33000, label: "رمضانی" },
            { y: 37000, label: "رمضانی" },
            { y: 32000, label: "رمضانی" },
            { y: 27500, label: "رمضانی" },
          ],
        },
        {
          type: "spline",
          dataPoints: [
            { y: 11500, label: "رمضانی" },
            { y: 10500, label: "رمضانی" },
            { y: 9000, label: "رمضانی" },
            { y: 13500, label: "رمضانی" },
            { y: 13890, label: "رمضانی" },
            { y: 18500, label: "رمضانی" },
            { y: 16000, label: "رمضانی" },
            { y: 14500, label: "رمضانی" },
          ],
        },
      ],
    };
    return (
      <div>
        <CanvasJSChart
          className="chartback"
          options={options}
          onRef={(ref) => (this.chart = ref)}
        />
        {/*You can get reference to the chart instance as shown above using onRef. This allows you to access all chart properties and methods*/}
      </div>
    );
  }
}

export default FirstChart;
